mapper.select:
SELECT s.SID, s.TID, s.SRID, s.AID, s.SNAME, s.MP3, s.IS_HOT, s.IS_NEW, s.PTIMES, s.AUDIT_STATUS, s.SHOW_STATUS, m.TNAME, sr.SRNAME, s.PIC, a.ANAME FROM song s LEFT JOIN mtype m ON s.TID = m.TID LEFT JOIN songer sr ON s.SRID = sr.SRID LEFT JOIN album a ON s.AID = a.AID
s.SNAME LIKE '%${sname}%'
AND sr.SRNAME LIKE '%${srname}%'
AND a.ANAME LIKE '%${aname}%'
AND s.TID = #{tid}
AND s.IS_HOT = #{isHot}
AND s.IS_NEW = #{isNew}
AND s.sid = #{sid}
LIMIT #{startNum}, #{pageSize}

