INSERT INTO `song` VALUES ('33', '1', '25', '38', '冠世一战', '/pic/70cbf33d-ba91-49e0-b744-ca7d29053f4d.PNG', '/mp3/bb45c8e7-804b-4334-a20d-1fe46a4d3d38.mp3', '[00:01.42]虽千万人逆我之 我仍执着\r\n[00:04.99]侠客行遍九州恩仇奔波\r\n[00:07.58]刀光剑影里是谁的明眸粲粲如星火\r\n[00:12.86]纵使我双掌倚天 苍龙伏获\r\n[00:15.62]奈何降不住这人心如魔\r\n[00:18.49]所谓天下第一 怎囿我 天高海阔\r\n[00:25.10]《冠世一战 ——2019bilibili拜年祭》\r\n[00:32.56]作/编曲：litterzy\r\n[00:33.27]作词：墨大筝\r\n[00:33.97]演唱：言和\r\n[00:34.59]调校：Creuzer\r\n[00:35.24]混音：CuTTleFish\r\n[00:35.92]笛子：水玥儿\r\n[00:36.63]古筝：墨韵随步摇\r\n[00:37.34]曲绘：骸鸦（黑茶别馆）&食莲丼（黑茶别馆）\r\n[00:38.62]水墨插画：周告斑&鹤庵波影&微生\r\n[00:39.32]视频：Xirokyo（麻薯映像）&残垣留梦（麻薯映像）\r\n[00:40.64]脚本：Oneways\r\n[00:41.32]标题书法：暴走的刺猬（麻薯映像）\r\n[00:42.00]监制：纳兰寻风\r\n[00:42.63]翻唱：鹿予\r\n[00:43.32]后期：NamieMeng\r\n[00:43.98]\r\n[00:53.08]飞雪连天 扬沙漫地华山巅 谁论剑\r\n[00:58.82]盖世绝学 宏图业 究竟能有多执念\r\n[01:04.28]狭路相逢 离离分分 游丝难织就双飞燕\r\n[01:09.10]天之道 损有余 所求仍不得圆\r\n[01:14.61]斗胆教日月射落\r\n[01:17.14]白针将剑招拆破\r\n[01:20.37]谁逐鹿 谁流离 谁问鼎 谁失所\r\n[01:23.20]此战 何必笑我\r\n[01:25.78]虽千万人逆我之 我仍执着\r\n[01:28.36]侠客行遍九州恩仇奔波\r\n[01:31.04]刀光剑影里是谁的明眸粲粲如星火\r\n[01:36.14]纵使我双掌倚天 苍龙伏获\r\n[01:39.23]奈何降不住这人心如魔\r\n[01:41.89]所谓天下第一 怎囿我 天高海阔\r\n[01:50.11]\r\n[02:06.40]左右逢源 大悟大彻黑白颠 谁周旋\r\n[02:11.92]书剑快意 仁无敌越是冠绝 越决绝\r\n[02:16.73]痴缠眷侣 生死相许 渺万里层云 千山雪\r\n[02:22.10]侠之道 为国为民敢向天下先\r\n[02:27.38]内力将乾坤腾挪\r\n[02:30.12]轻功点水上烟波\r\n[02:33.06]阴与阳 柔与刚 强与弱 对与错\r\n[02:36.08]此战 我战与我\r\n[02:38.38]虽千万人逆我之 我仍执着\r\n[02:41.32]侠客行遍九州恩仇奔波\r\n[02:43.73]刀光剑影里是谁的明眸粲粲如星火\r\n[02:48.91]纵使我双掌倚天 苍龙伏获\r\n[02:52.00]奈何降不住这人心如魔\r\n[02:54.76]所谓天下第一 怎囿我 天高海阔\r\n[03:02.27]\r\n[03:39.98]所幸敝屣荣华且把酒\r\n[03:46.07]风雨偏洗岳阳楼\r\n[03:51.34]冠世侠者先天下之忧\r\n[03:56.58]此战 再出手\r\n[03:59.83]虽千万人逆我之 我仍执着\r\n[04:02.30]寒铓再出江湖付身家国\r\n[04:04.84]踏破贺兰山缺 君自有 还我山河胆魄\r\n[04:10.08]纵使我双掌倚天 苍龙伏获\r\n[04:12.90]拼此生 尚不知 是功是过\r\n[04:15.59]若非冠世一战 后人何处听传说', '1', '1', null, null, null);

